import React from 'react';
import { Link ,useNavigate} from 'react-router-dom';
import CustomHook from './../Hooks/CustomHook.jsx';

const LoginCompo = () => {
    const navigate = useNavigate();
    const [loginError,setLoginError] = useState(false);
    const { handleChange, inp, errors } = CustomHook({ username: "", password: "" }, {})
    const [cookies, setCookie] = useCookies(['name']);
    const login = (event) => {
        event.preventDefault()
        // console.log("inp Data", inp);
        // axios.get('http://localhost:5000/users?username=test&password=123').then(function (response) { console.log(response); })
        // axios.get('http://localhost:5000/users?username='+inp.username+'&password='+inp.password).then(function (response) { console.log(response); })
        axios.get(`http://localhost:5000/users?username=${inp.username}&password=${inp.password}`).then(function (response) { 
            // console.log(response.data.length);
            if (response.data.length > 0) {
                localStorage.setItem('itemsLocalStorage', JSON.stringify({"data":"itemsLocalStorage"}));
                sessionStorage.setItem('itemsSessionStorage', JSON.stringify({"data":"itemsSessionStorage"}));
                setCookie('loggedin', "active");
                // console.log(response.data[0].role);
                if (response.data[0].role == 1) {
                    console.log("inside if");
                    setCookie('role', "1");
                    navigate("/admin")
                } else {
                    navigate("/")
                    setCookie('role', "2");
                    console.log("inside else");
                }
                setLoginError(false)
            } else {
                setLoginError(true)
            } 
        })
    }
    return (
        <>
            <div className="row mt-5 ">
                <div className="col-4 shadow bg-body rounded offset-4 mt-5">
                    <div className="card ">
                        <div className="card-header text-center"> <h3>Login</h3></div>
                        <div className="card-body">
                            <form method="post">
                                <div className="row">
                                    <div className="col">
                                        <input type="text" onChange={handleChange} onBlur={handleChange} placeholder='Enter your name' className='form-control' name="username" />
                                        {/* {JSON.stringify(inp)} */}
                                        {/* {JSON.stringify(error)} */}
                                    </div>
                                </div>
                                <div className="row mt-3">
                                    <div className="col">
                                        <input type="text" onChange={handleChange} placeholder='Enter your password' className='form-control'  name="password"/>
                                    </div>
                                </div>
                                <div className="row mt-3 text-center">
                                    <div className="col">
                                        <input type="submit" className='btn btn-primary mx-4' value="login" />
                                        <input type="reset" className='btn btn-danger ' value="cancel" />
                                    </div>
                                </div>
                                <div className="row mt-3 text-center">
                                    <div className="col">
                                        <Link to="/rigistration">Creact a new account</Link>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div >
        </>
    );
};

export default LoginCompo;
